// Guest Reducer = Guest Store
// Holds the state
import {
  GUEST_LOADING,
  GUEST_LOADED,
  REGISTER_SUCCESS
} from "../actions/types";

// This is the store, holds information of the guest
const intialState = {
  isLoading: false,             // if the guest is still being loaded from backend
  guest: null                   // the guest itself
}

export default function (state = intialState, action) {
  switch (action.type) {
    case GUEST_LOADING:
      return {
        ...state,
        isLoading: true
      };

    case GUEST_LOADED:
      return {
        ...state,
        isLoading: false,
        guest: action.payload
      }

    case REGISTER_SUCCESS:
      return {
        ...state,
        ...action.payload,
        isLoading: false,
        guest: action.payload
      }

    default:
      return state;
  }
}
