import axios from 'axios';
import {
  GUEST_LOADING,
  GUEST_LOADED,
  GUEST_SUCCESS
} from "./types";

export const loadGuest = () => (dispatch) => {
  // Dispatch calls the appropiate reducer, in this case load the guest from the backend
  dispatch({ type: GUEST_LOADING });

  // Load the guest from the backend
  axios.get('/api/guests')
    .then(guest => dispatch({
      type: GUEST_LOADED,               // tell the reducer the guest is loaded 
      payload: guest.data              // store the guest in the reducer store
    }));
}

export const createGuest = () => (dispatch) => {
  axios.post('/api/guests')
    .then(guest => dispatch({
      type: GUEST_SUCCESS,
      payload: guest.data
    }));
}
